sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	var currency = {
		decimals: 2,
		postfix: ' '+current.portfolio.currency
		};

	var config = {
		performance: {
			decimals: 2,
			postfix: ' %'
		},
		amount: { decimals: 0 },
		value: currency,
		gain: currency,
		costbase: currency,
		price_best: currency,
		price_avg: currency,
		gain: currency
	}

	x_snc_devtools.ExecuteBusinessRuleNumberFormat(current,config);

})(current, previous);
