sys_variable_value.value:
(function(outputs, steps, stepResult, assertEqual)
{
	var test = new x_snc_devtools.TestAPI(outputs, steps, stepResult, assertEqual);
	var RenderValue = x_snc_devtools.RenderValue;
	var GetRecord = x_snc_devtools.GetRecord;
	var RecordSetValue = x_snc_devtools.RecordSetValue;
	var CurrencyConversion = x_snc_devtools.CurrencyConversion;

	
	test.SetResult(true);


	function TestCase_GetBestPriceForInstrument(strInstrumentSysId, strCurrencySysId, vExpected)
	{
		function TestCase_GetBestPriceForInstrument_CheckResult(vInput,vExpected,vResult)
		{
			return x_snc_devtools.IsEqual(vExpected,vResult);
		}

		test.TestCase(
			'Function GetBestPriceForInstrument()',
			GetBestPriceForInstrument,
			{
				'Instrument': strInstrumentSysId,
				'Currency': strCurrencySysId,
			},
			vExpected,
			TestCase_GetBestPriceForInstrument_CheckResult);
	}


	TestCase_GetBestPriceForInstrument(null,null,false);
	TestCase_GetBestPriceForInstrument(null,'EUR',false);
	TestCase_GetBestPriceForInstrument(null,'USD',false);
	
	
	var grIssuer = new GlideRecord('x_snc_investor_issuer');
	grIssuer.initialize();
	grIssuer.name = '_ATF_INVESTOR_TEST';
	var strIssuerSysId = grIssuer.insert();
	
	var grInstrument = new GlideRecord('x_snc_investor_instrument');
	grInstrument.initialize();
	grInstrument.name = '_ATF_INVESTOR_TEST';
	grInstrument.type= 'stock';
	grInstrument.issuer = strIssuerSysId;
	var strInstrumentSysId = grInstrument.insert();
	
	TestCase_GetBestPriceForInstrument(strInstrumentSysId,null,false);
	TestCase_GetBestPriceForInstrument(strInstrumentSysId,'EUR',false);
	TestCase_GetBestPriceForInstrument(strInstrumentSysId,'USD',false);

	
	var grNotation = new GlideRecord('x_snc_investor_notation');
	grNotation.initialize();
	grNotation.instrument = strInstrumentSysId;
	grNotation.currency = 'EUR';
	grNotation.symbol = 'TEST_EUR_1';
	var strNotationEUR1SysId = grNotation.insert();

	var grNotation = new GlideRecord('x_snc_investor_notation');
	grNotation.initialize();
	grNotation.instrument = strInstrumentSysId;
	grNotation.currency = 'EUR';
	grNotation.symbol = 'TEST_EUR_2';
	var strNotationEUR2SysId = grNotation.insert();
	
	var grNotation = new GlideRecord('x_snc_investor_notation');
	grNotation.initialize();
	grNotation.instrument = strInstrumentSysId;
	grNotation.currency = 'USD';
	grNotation.symbol = 'TEST_USD';
	var strNotationUSD1SysId = grNotation.insert();	
	
	TestCase_GetBestPriceForInstrument(strInstrumentSysId,null,false);
	TestCase_GetBestPriceForInstrument(strInstrumentSysId,'EUR',false);
	TestCase_GetBestPriceForInstrument(strInstrumentSysId,'USD',false);

	
	var grPriceDaily = new GlideRecord('x_snc_investor_price_daily');
	grPriceDaily.initialize();
	grPriceDaily.notation = strNotationEUR1SysId;
	grPriceDaily.date = '1500-01-01';
	grPriceDaily.close = 80;
	var strPriceDailyEUR1_1SysId = grPriceDaily.insert();
	RecordSetValue('x_snc_investor_notation',strNotationEUR1SysId,'last_price_daily',strPriceDailyEUR1_1SysId);

	var grPriceDaily = new GlideRecord('x_snc_investor_price_daily');
	grPriceDaily.initialize();
	grPriceDaily.notation = strNotationEUR1SysId;
	grPriceDaily.date = '1500-01-02';
	grPriceDaily.close = 100;
	var strPriceDailyEUR1_2SysId = grPriceDaily.insert();
	RecordSetValue('x_snc_investor_notation',strNotationEUR1SysId,'last_price_daily',strPriceDailyEUR1_2SysId);
	
	
	var grPriceDaily = new GlideRecord('x_snc_investor_price_daily');
	grPriceDaily.initialize();
	grPriceDaily.notation = strNotationUSD1SysId;
	grPriceDaily.date = '1500-01-02';
	grPriceDaily.close = CurrencyConversion('EUR','USD',100,grPriceDaily.date);
	var strPriceDailyUSD1_2SysId = grPriceDaily.insert();
	RecordSetValue('x_snc_investor_notation',strNotationUSD1SysId,'last_price_daily',strPriceDailyUSD1_2SysId);
	
	
	
	test.Log(RenderValue(GetIssuerRecord(strIssuerSysId)));
	test.Log(RenderValue(GetInstrumentRecord(strInstrumentSysId)));
	test.Log(RenderValue(GetNotationRecord(strNotationEUR1SysId)));
	test.Log(RenderValue(GetNotationRecord(strNotationEUR2SysId)));
	test.Log(RenderValue(GetNotationRecord(strNotationUSD1SysId)));

	test.Log(RenderValue(GetRecord('x_snc_investor_price_daily',strPriceDailyEUR1_1SysId)));
	test.Log(RenderValue(GetRecord('x_snc_investor_price_daily',strPriceDailyEUR1_2SysId)));
	test.Log(RenderValue(GetRecord('x_snc_investor_price_daily',strPriceDailyUSD1_2SysId)));
	
	TestCase_GetBestPriceForInstrument(strInstrumentSysId,null,false);
	TestCase_GetBestPriceForInstrument(strInstrumentSysId,'EUR',
		{
			date: '1500-01-02',
			price: 100,
			type: 'close'
		});
	
	return test.GetResult();
	
})(outputs, steps, stepResult, assertEqual);
