sys_script_include.script:
function NotationSetLatestDailyPrice(strNotationSysId)
{
	var grNotation = GetNotationRecord(strNotationSysId);
	if (grNotation == false)
	{
		return false;
	}

	var grPricesDaily = new GlideRecord('x_snc_investor_price_daily');
	grPricesDaily.addQuery('notation','=',strNotationSysId);
	grPricesDaily.orderByDesc('date');
	grPricesDaily.setLimit(1);
	grPricesDaily.query();
	if (grPricesDaily.next() == false)
	{
		return false;
	}

	grNotation.last_price_daily = grPricesDaily.sys_id;
	grNotation.update();
	return true;
}
