sys_script_include.script:
function LoadPricesDailyForNotation(strNotationSysId)
{
	function Log(strText)
	{
		x_snc_investor.Log('LoadPricesDailyForNotation: '+strText);
	}

	var IsValidRecord = x_snc_devtools.IsValidRecord;
	var GetStringValue = x_snc_devtools.GetStringValue;

	var grNotation = GetNotationRecord(strNotationSysId);
	if (IsValidRecord(grNotation) == false)
	{
		return false;
	}


	var strSymbol = GetStringValue(grNotation.symbol);
	var alphavantage = new AlphaVantageAPI('EDLVQWD4VABWC11E');
	var aPrices = alphavantage.GetAdjustedDailyPricesForSymbol(strSymbol); 
	if (aPrices == false)
	{
		return false;
	}

	for (var nPrice = 0; nPrice < aPrices.length; nPrice++)
	{
		var price = aPrices[nPrice];

		var grPricesDaily = new GlideRecord('x_snc_investor_price_daily');
		grPricesDaily.addQuery('notation','=',strNotationSysId);
		grPricesDaily.addQuery('date','=',price.date);
		grPricesDaily.query();
		if (grPricesDaily.next() == false)
		{
			grPricesDaily.initialize();
			grPricesDaily.notation = strNotationSysId;
			grPricesDaily.date = price.date;
			grPricesDaily.open = price.open;
			grPricesDaily.high = price.high;
			grPricesDaily.low = price.low;
			grPricesDaily.close = price.close_adjusted;
			grPricesDaily.dividend = price.dividend;
			grPricesDaily.split = price.split;
			grPricesDaily.volume = price.volume;
			grPricesDaily.insert();
		}
		else
		{
			grPricesDaily.open = price.open;
			grPricesDaily.high = price.high;
			grPricesDaily.low = price.low;
			grPricesDaily.close = price.close_adjusted;
			grPricesDaily.dividend = price.dividend;
			grPricesDaily.split = price.split;
			grPricesDaily.volume = price.volume;
			grPricesDaily.update();
		}
	}

	return true;
}
