sys_script_include.script:
function LoadPricesDaily()
{
	function Log(strText)
	{
		x_snc_investor.Log('LoadMarketData: LoadPricesDaily: '+strText);
	}


	var grNotation = new GlideRecord('x_snc_investor_notation');
	grNotation.query();
	while(grNotation.next())
	{
		var bSuccess = LoadPricesDailyForNotation(grNotation.sys_id);
		Log('LoadPricesDailyForNotation: '
			+grNotation.instrument.issuer.name+' / '
			+grNotation.instrument.isin+' / '
			+grNotation.exchange.name+' / '
			+grNotation.symbol+': '
			+bSuccess);
	}
}
