sys_script_include.script:
function CreateInvestorTask(strTopic, strUserSysId, strPortfolioSysId, strInstrumentSysId)
{
	var GetStringValue = x_snc_devtools.GetStringValue;

	var grPortfolio = GetPortfolioRecord(strPortfolioSysId);
	var grInstrument = GetInstrumentRecord(strInstrumentSysId);


	strTopic = GetStringValue(strTopic);
	var strShortDescription = 'xxx'; 

	if (strTopic == 'loss')
	{
		strShortDescription = 'Instrument '+grInstrument.isin+' in portfolio "'+grPortfolio.name+'" dropped below 80%'; 
	}
	else if (strTopic == 'peak')
	{
		strShortDescription = 'Instrument '+grInstrument.isin+' in portfolio "'+grPortfolio.name+'" passed a peak'; 
	}
	else
	{
		strShortDescription = 'Instrument '+grInstrument.isin+' in portfolio "'+grPortfolio.name+'" should be reviewed'; 
		strTopic = 'unknown';
	}


	var grTask = new GlideRecord('x_snc_investor_task');
	grTask.initialize();
	grTask.assigned_to = strUserSysId;
	grTask.portfolio = strPortfolioSysId;
	grTask.instrument = strInstrumentSysId;
	grTask.short_description = strShortDescription;
	grTask.topic = strTopic;
	return grTask.insert();
}
