sys_script_include.script:
function CalculateKeyFiguresForPriceDaily(strPriceDailySysId)
{
	Debug('CalculateKeyFiguresForPriceDaily('+strPriceDailySysId+')');
	var GetRecord = x_snc_devtools.GetRecord;
	var GetStringValue = x_snc_devtools.GetStringValue;
	var GetFloatValue = x_snc_devtools.GetFloatValue;

	strPriceDailySysId = GetStringValue(strPriceDailySysId);
	var grPriceDaily = GetRecord('x_snc_investor_price_daily',strPriceDailySysId);
	if (grPriceDaily == false)
	{
		return false;
	}

	// SMA 38
	Debug('SMA 38');
	grPriceDaily.sma38 = '';
	var grPriceDailyPast = new GlideRecord('x_snc_investor_price_daily');
	grPriceDailyPast.addQuery('notation','=',grPriceDaily.notation);
	grPriceDailyPast.addQuery('date','<=',grPriceDaily.date);
	grPriceDailyPast.orderByDesc('date');
	grPriceDailyPast.setLimit(38);
	grPriceDailyPast.query();
	if (grPriceDailyPast.getRowCount() == 38)
	{
		var aValues = [];
		while (grPriceDailyPast.next())
		{
			aValues.push(GetFloatValue(grPriceDailyPast.close));
		}
		if (aValues.length == 38)
		{
			Debug('CalculateKeyFigureSimpleMovingAverage we have 38 values!');
			grPriceDaily.sma38 = CalculateKeyFigureSimpleMovingAverage(aValues);
		}
	}


	// SMA 200
	Debug('SMA 200');
	grPriceDaily.sma200 = '';
	var grPriceDailyPast = new GlideRecord('x_snc_investor_price_daily');
	grPriceDailyPast.addQuery('notation','=',grPriceDaily.notation);
	grPriceDailyPast.addQuery('date','<=',grPriceDaily.date);
	grPriceDailyPast.orderByDesc('date');
	grPriceDailyPast.setLimit(200);
	grPriceDailyPast.query();
	if (grPriceDailyPast.getRowCount() == 200)
	{
		var aValues = [];
		while (grPriceDailyPast.next())
		{
			aValues.push(GetFloatValue(grPriceDailyPast.close));
		}
		if (aValues.length == 200)
		{
			Debug('CalculateKeyFigureSimpleMovingAverage we have 200 values!');
			grPriceDaily.sma200 = CalculateKeyFigureSimpleMovingAverage(aValues);
		}
	}	

	grPriceDaily.update();

	return true;
}
