sys_script_include.script:
function DevToolsGetFormatConfig(grRecord)
{
	var strTable = grRecord.getRecordClassName();

	if (strTable == 'x_snc_investor_portfolio')
	{

		var currency = {
			decimals: 2,
			postfix: ' '+grRecord.currency
			};

		var formatconfig = {
			performance: {
				decimals: 2,
				postfix: ' %'
			},
			value: currency,
			gain: currency,
			cashflow: currency,
			realized_profit: currency,
			realized_loss: currency,
			realized_total: currency
			};		

		return formatconfig;
	}

	if (strTable == 'x_snc_investor_position')
	{
		var currency = {
			decimals: 2,
			postfix: ' '+grRecord.portfolio.currency
			};

		var formatconfig = {
			performance: {
				decimals: 2,
				postfix: ' %'
			},
			amount: { decimals: 0 },
			value: currency,
			gain: currency,
			costbase: currency,
			price_best: currency,
			price_avg: currency,
			gain: currency
			};

		return formatconfig;
	}

	if (strTable == 'x_snc_investor_transaction')
	{
		var currency = {
			decimals: 2,
			postfix: ' '+grRecord.portfolio.currency
			};

		var formatconfig = {
			total_price: currency,
			cashflow: currency
			};

		return formatconfig;
	}


	return false;
}
