sys_script_include.script:
function DevToolsGetFormatConfig(grRecord)
{
	var strTable = grRecord.getRecordClassName();

	var performance =
		{
			decimals: 2,
			postfix: ' %'
		};

	if (strTable == 'x_snc_investor_portfolio')
	{

		var currency = {
			decimals: 2,
			postfix: ' '+grRecord.currency
			};

		var formatconfig = {
			performance: performance,
			value: currency,
			gain: currency,
			cashflow: currency,
			realized_profit: currency,
			realized_loss: currency,
			realized_total: currency
			};		

		return formatconfig;
	}

	if (strTable == 'x_snc_investor_position')
	{
		var currency = {
			decimals: 2,
			postfix: ' '+grRecord.portfolio.currency
			};

		var formatconfig = {
			performance: performance,
			amount: { decimals: 3 },
			value: currency,
			gain: currency,
			costbase: currency,
			price_best: currency,
			price_avg: currency,
			gain: currency
			};

		return formatconfig;
	}

	if (strTable == 'x_snc_investor_transaction')
	{
		var currency = {
			decimals: 2,
			postfix: ' '+grRecord.portfolio.currency
			};

		var formatconfig = {
			total_price: currency,
			cashflow: currency,
			price: currency,
			charges: currency
			};

		return formatconfig;
	}

	if (strTable == 'x_snc_investor_event')
	{	
		var formatconfig = {};
		if (grRecord.event == 'performance')
		{
			formatconfig['value'] = performance;
		}
		else if (grRecord.event == 'split')
		{
			formatconfig['value'] = { decimals: 3 };
		}	
		return formatconfig;
	}	

	if (strTable == 'x_snc_investor_price_daily')
	{
		var currency = {
			decimals: 2,
			postfix: ' '+grRecord.notation.currency
			};

		var formatconfig = {
			open: currency,
			high: currency,
			low: currency,
			close: currency,
			close_adjusted: currency,
			sma38: currency,
			sma90: currency,
			sma200: currency,
			dividend: currency,
			volume: { decimals: 0 },
			split: { decimals: 4 }
			};

		return formatconfig;
	}	

	if (strTable == 'x_snc_investor_notation')
	{
		var currency = {
			decimals: 2,
			postfix: ' '+grRecord.currency
			};

		var formatconfig = {
			'last_price_daily.close': currency
			};

		return formatconfig;
	}	


	return false;
}
