sys_script_include.script:
function GetBestPriceForInstrument(strInstrumentSysId,strCurrencySysId)
{
	var GetRecord = x_snc_devtools.GetRecord;
	var IsValidRecord = x_snc_devtools.IsValidRecord;
	var CurrencyConversion = x_snc_devtools.CurrencyConversion;

	var bestprice = false;

	// try matching currency first
	var grNotation = new GlideRecord('x_snc_investor_notation');
	grNotation.addQuery('instrument','=',strInstrumentSysId);
	grNotation.addQuery('currency','=',strCurrencySysId);
	grNotation.addQuery('last_price_daily','!=','');
	grNotation.orderByDesc('last_price_daily.date');
	grNotation.query();
	if (grNotation.next())
	{
		var grLastPriceDaily = GetRecord('x_snc_investor_price_daily',grNotation.last_price_daily);
		if (IsValidRecord(grLastPriceDaily))
		{
			bestprice = {};
			bestprice.price = grLastPriceDaily.close;
			bestprice.date = grLastPriceDaily.date;
			bestprice.type = 'close';
			return bestprice;
		}
	}

	// check alternative currencies
	var grNotation = new GlideRecord('x_snc_investor_notation');
	grNotation.addQuery('instrument','=',strInstrumentSysId);
	grNotation.addQuery('currency','!=',strCurrencySysId);
	grNotation.addQuery('last_price_daily','!=','');
	grNotation.orderByDesc('last_price_daily.date');
	grNotation.query();
	if (grNotation.next())
	{
		var grLastPriceDaily = GetRecord('x_snc_investor_price_daily',grNotation.last_price_daily);
		if (IsValidRecord(grLastPriceDaily))
		{
			if (bestprice != false) // we already have a price
			{
				if (bestprice.date < grLastPriceDaily.date) // do we have a price that is more recent?
				{
					bestprice.price = grLastPriceDaily.close;
					bestprice.date = grLastPriceDaily.date;
					bestprice.type = 'close';
					return bestprice;
				}
			}
			else
			{
				bestprice = {};
				bestprice.price = CurrencyConversion(grNotation.currency,strCurrencySysId,grLastPriceDaily.close);
				bestprice.date = grLastPriceDaily.date;
				bestprice.type = 'close';
				return bestprice;

			}
		}
	}


	return false;
}
